INSERT INTO questionList (subjID, questionText, option1, option2, option3, option4, correctAnswer, correctExplanation, difficulty) VALUES
(1, 'What is the place value of 4 in 341,602?', 'Thousands', 'Ten thousands', 'Hundreds', 'Hundred thousands', 'Ten thousands', 'The 4 is in the second position from the left in a six-digit number, which is the ten thousands place.', 'Hard'),
